import React, { useState, useEffect, useContext } from 'react';
import PropTypes from 'prop-types';
import { useHistory } from 'react-router';
import whiteHeartIcon from '../images/whiteHeartIcon.svg';
import blackHeartIcon from '../images/blackHeartIcon.svg';
import setFavoritos, { isfavoriteRecipe } from '../services/setFavoritos';
import getInformation from '../services/getInformation';
import RecipeContext from '../context/RecipeContext';

function FavoriteBtn({ objDetail, urlText, id }) {
  const [isFavorite, setIsFavorite] = useState(false);
  const { receitasFav, setReceitasFav } = useContext(RecipeContext);

  const history = useHistory();
  const currentUrl = history.location.pathname;

  useEffect(() => {
    isfavoriteRecipe(id, setIsFavorite);
  }, []);

  const removeFavorite = () => {
    const favoriteRecipes = JSON.parse(localStorage.getItem('favoriteRecipes'));
    const updatedFavorites = favoriteRecipes.filter((elem) => elem.id !== id);
    localStorage.setItem('favoriteRecipes', JSON.stringify(updatedFavorites));
    isfavoriteRecipe(id, setIsFavorite);
  };

  const onClickFavorite = () => {
    if (isFavorite === true) {
      return removeFavorite();
    }
    return setFavoritos(objDetail[0], urlText, id, setIsFavorite);
  };

  const updateFav = () => {
    if (currentUrl.includes('receitas-favoritas')) {
      const filtered = receitasFav.filter((elem) => elem.id !== id);
      setReceitasFav(filtered);
      setIsFavorite(true);
    }
  };

  return (
    <input
      className="favorite-btn"
      onClick={ () => { onClickFavorite(); updateFav(); } }
      type="image"
      data-testid="favorite-btn"
      src={ isFavorite ? blackHeartIcon : whiteHeartIcon }
      alt="foto do item"
    />
  );
}

FavoriteBtn.propTypes = {
  objDetail: PropTypes.shape(
    PropTypes.any,
  ).isRequired,
  urlText: PropTypes.string.isRequired,
  id: PropTypes.string.isRequired,
};

export default FavoriteBtn;
