import React, { useContext } from 'react';
import PropTypes from 'prop-types';
import { useHistory } from 'react-router-dom';
import RecipeContext from '../context/RecipeContext';

function ButtonIniciar({ id, objDetail }) {
  const { startedRecipes } = useContext(RecipeContext);
  const history = useHistory();

  const Ingredients = () => {
    const ingredientes = Object.entries(objDetail[0]);

    const filtering = ingredientes.filter((element) => (
      element[0].includes('strIngredient') && element[1] !== null && element[1] !== ''));

    const results = filtering.map((elem) => elem[1]);
    return results;
  };

  const value = history.location.pathname;

  const setLocalStoragesIngredients = () => {
    if (localStorage.getItem('inProgressRecipes') !== undefined ) {
      const sem = localStorage.getItem('inProgressRecipes');
      const inProgress = JSON.parse(sem);
      if (value.includes('comidas')) {
        localStorage.setItem('inProgressRecipes', JSON.stringify({
          ...inProgress,
          meals: {
            ...inProgress.meals,
            [id]: Ingredients(),
          },
        }));
      }
      if (value.includes('bebidas')) {
        localStorage.setItem('inProgressRecipes', JSON.stringify({
          ...inProgress,
          cocktails: {
            ...inProgress.cocktails,
            [id]: Ingredients(),
          },
        }));
      }
    }
  };

  const verifyId = (id) => {
    const inProgress = JSON.parse(localStorage.getItem('inProgressRecipes'));
    const entries = Object.entries((inProgress));
    const newArray = [
      ...Object.keys(inProgress.meals),
      ...Object.keys(inProgress.cocktails),
    ];
    console.log(newArray)
    // return entries.forEach((elem) => {
    //   return Object.entries(elem).forEach((e) => e);
    // });
  };

  return (
    <div>
      {(verifyId('52978'))}
      <button
        className="start-recipe-btn"
        type="button"
        data-testid="start-recipe-btn"
        onClick={ setLocalStoragesIngredients }
      >
        {startedRecipes.some((elem) => elem === id)
          ? 'Continuar Receita' : 'Iniciar Receita' }
      </button>
    </div>
  );
}

ButtonIniciar.propTypes = {
  id: PropTypes.string.isRequired,
  objDetail: PropTypes.shape(
    PropTypes.any,
  ).isRequired,
};

export default ButtonIniciar;
